    // };
    case "DECREMENT":
      return {
        counter: state.counter - 1,
      };

    case "ADD":
      return {
        counter: state.counter + action.payload,
      };
    case "SUBTRACT":
      return {
        counter: state.counter - action.payload,
      };
    case "STORE_RESULT":
      return;
