const initialState = {
  counter: 0,
};

const reducer = (state = initialState, action) => {
  if (action.type === "INCREMENT") {
    return {
      counter: state.counter + 1,
    };
  }
  //DECREMENT
  if (action.type === "DECREMENT") {
    return {
      counter: state.counter - 1,
    };
  }

  //ADD
  if (action.type === "ADD") {
    return {
      counter: state.counter + 5,
    };
  }
  //SUBTRACT
  if (action.type === "SUBTRACT") {
    return {
      counter: state.counter - 5,
    };
  }

  return state;
};

export default reducer;
